vf37m93s3q9gfvf0luaggur" MODIFIED="1545706367353" TEXT="サーバ室の整備費用"/><node CREATED="1545706367353" ID="0t1pgjecnlgcdgog4g64trr9se" MODIFIED="1545706367353" TEXT="ネットワーク回線の敷設費用"/><node CREATED="1545706367353" ID="1t1jn3vvqupprnq42h6cuau2mm" MODIFIED="1545706367353" TEXT="ネットワーク機器の購入費用"/><node CREATED="1545706367353" ID="776sifehdp9dokd9hetr957sib" MODIFIED="1545706367353" TEXT="サーバの購入費用"/><node CREATED="1545706367353" ID="6k6soj011susjcsae9n9qi9tge" MODIFIED="1545706367353" TEXT="ウェブアプリケーションの開発費用・購入費用"/><node CREATED="1545706367353" ID="5f4skt96ms16f1q4kg030v9fau" MODIFIED="1545706367353" TEXT="ウェブサーバの構築費用"/><node CREATED="1545706367353" ID="64luotaq0t1slr5par972889tj" MODIFIED="1545706367353" TEXT="ウェブサイトの運用管理費用"/><node CREATED="1545706367353" ID="7nccd9pddjgg8p5d529ls77f7m" MODIFIED="1545706367353" TEXT="サービス利用費用、課金"/></node><node CREATED="1545706367353" ID="0uqsqfcc3m0jsfnmohnnkge0qd" MODIFIED="1545706367353" TEXT="2.5. 運営形態毎の責任範囲"><node CREATED="1545706367353" ID="7mjkjujkh76jdq97thgi75adho" MODIFIED="1545706367353" TEXT="表2-5-1 運営形態毎の責任範囲"/><node CREATED="1545706367353" ID="5i8pq0uvujoekjo097c1mv8pju" MODIFIED="1545706367353" TEXT="サービスの停止"/><node CREATED="1545706367353" ID="0a8eh693f2tqifa2keb5svjdlq" MODIFIED="1545706367353" TEXT="データの破壊・消去"/><node CREATED="1545706367353" ID="69evc2jh1pl9mc7r5l8mc8jjfg" MODIFIED="1545706367353" TEXT="情報漏洩"/><node CREATED="1545706367353" ID="6ij24di1j8eo54fmbb73op85p2" MODIFIED="1545706367353" TEXT="改ざん"/></node><node CREATED="1545706367353" ID="0i95l80a6n3j0t75lgd11ab5ej" MODIFIED="1545706367353" TEXT="【コラム】 「契約の免責事項"><node CREATED="1545706367353" ID="4p6tj85d05fm76auv0enrds3fq" MODIFIED="1545706367353" TEXT="メンテナンスまたは不慮の事故等により、サービス停止等によるサービス利用者の逸失利益や損害について、提供者は一切の責任を負わないものとする"/><node CREATED="1545706367353" ID="6logdugjp639317cbm4ai051bo" MODIFIED="1545706367353" TEXT="不測の事故等により、サービス利用者のサーバ上に蓄積されているデータが喪失、流失、損壊等が発生した場合、提供者は一切の責任を負わないものとする"/></node><node CREATED="1545706367353" ID="75n5deid46ltd0ej6auq373l0p" MODIFIED="1545706367353" TEXT="2.6. 各運営形態で検討が必要なセキュリティ対策"><node CREATED="1545706367353" ID="3jf7gt2om0b0g797q29s4vegia" MODIFIED="1545706367353" TEXT="表2-6-1 運営形態毎に検討すべきセキュリティ対策"/><node CREATED="1545706367353" ID="6dbbirh7pfuddi8t2tmo3dhia9" MODIFIED="1545706367353" TEXT="セキュリティ対策項目"><node CREATED="1545706367353" ID="0rpqpdmi1lqtftapt3itbffvva" MODIFIED="1545706367353" TEXT="システムセキュリティ対策"><node CREATED="1545706367353" ID="2a3o5ctgeqcv3o4vjs6nj09a4n" MODIFIED="1545706367353" TEXT="技術的対策"><node CREATED="1545706367353" ID="027s8tm5n4mo8rusj3auvbsn5g" MODIFIED="1545706367353" TEXT="物理"><node CREATED="1545706367353" ID="7tpv20d5tl0v811i2q6vg5bg2j" MODIFIED="1545706367353" TEXT="・サーバ室"/><node CREATED="1545706367353" ID="69m6hoqm1c58b6oprn0ouneg6s" MODIFIED="1545706367353" TEXT="・入退管理"/></node><node CREATED="1545706367353" ID="625mu6bkf1dmt5cts1ha1uqbb7" MODIFIED="1545706367353" TEXT="ネットワーク"><node CREATED="1545706367353" ID="6jarp14ekr7eb49gignf18afh7" MODIFIED="1545706367353" TEXT="・FW　・IDS/IPS"/><node CREATED="1545706367353" ID="7dnu80t93p0t1gnnfaahbfl381" MODIFIED="1545706367353" TEXT="・WAF ・VPN"/><node CREATED="1545706367353" ID="0bkmc96vv4eijiaeil6j64da2l" MODIFIED="1545706367353" TEXT="・ウイルス対策製品"/><node CREATED="1545706367353" ID="1qviugm6acptk07f07t1kh1hvn" MODIFIED="1545706367353" TEXT="・サンドボックス型製品"/><node CREATED="1545706367353" ID="6hfeu1gj4sa72mu5339ihgs2aq" MODIFIED="1545706367353" TEXT="・DDoS対策"/></node><node CREATED="1545706367353" ID="0ieebpvh8edbp6qjrqf62gu5oo" MODIFIED="1545706367353" TEXT="アプリケーション"><node CREATED="1545706367353" ID="75hffbpnq2f50iglfohclp4fsn" MODIFIED="1545706367353" TEXT="・改ざん検知"/><node CREATED="1545706367353" ID="01bduomato327r2tb0b0mvgnk0" MODIFIED="1545706367353" TEXT="・認証　・アクセス制御"/><node CREATED="1545706367353" ID="0m18gq80beetv6bol56vb8qr57" MODIFIED="1545706367353" TEXT="・データ保護"/></node></node><node CREATED="1545706367353" ID="4cf52fno3j0oluigniv1rk1ubb" MODIFIED="1545706367353" TEXT="運用管理的対策"><node CREATED="1545706367353" ID="1auiff96bioga1lc9m7t7kahia" MODIFIED="1545706367353" TEXT="セキュリティパッチ"><node CREATED="1545706367353" ID="5g4fhkjiq88vjcuq6fmsbagq6n" MODIFIED="1545706367353" TEXT="・パッチ適用"/><node CREATED="1545706367353" ID="3aq8i29s862qojg59t2ejv6mor" MODIFIED="1545706367353" TEXT="・仮想パッチ適用"/></node><node CREATED="1545706367353" ID="0gmlokpcb68s7v83bjabsa8b89" MODIFIED="1545706367353" TEXT="監視"><node CREATED="1545706367353" ID="54hb6n0v9qccc81lbse96k36u3" MODIFIED="1545706367353" TEXT="・ログ収集、分析"/></node><node CREATED="1545706367353" ID="2hlp8qvkqb9ibuagc7shg242ta" MODIFIED="1545706367353" TEXT="インシデント対応"><node CREATED="1545706367353" ID="3i3df740dpt7e33ta5nhf55hcv" MODIFIED="1545706367353" TEXT="・バックアップ"/><node CREATED="1545706367353" ID="6nb9nihe556ltt1hl7au26icka" MODIFIED="1545706367353" TEXT="・切り分け　　・抜線"/></node></node><node CREATED="1545706367353" ID="1c6mnap3ma3imgjts21b2j0d8h" MODIFIED="1545706367353" TEXT="人的対策"><node CREATED="1545706367353" ID="4i7d8b3l3inbf24k3n254h5fbd" MODIFIED="1545706367353" TEXT="要員教育"><node CREATED="1545706367353" ID="1is7p5dqjphr8ne3j9h30q7h8e" MODIFIED="1545706367353" TEXT="・ポリシー教育"/><node CREATED="1545706367353" ID="7ogvvil5dfprla00ar2uplr32n" MODIFIED="1545706367353" TEXT="・技術教育"/></node></node></node><node CREATED="1545706367353" ID="19dutjnk0q7uihlllm4777imc8" MODIFIED="1545706367353" TEXT="業務セキュリティ対策"><node CREATED="1545706367353" ID="2c0sioes5su1rle58ffn0apefp" MODIFIED="1545706367353" TEXT="社員教育"><node CREATED="1545706367353" ID="53jtd84ifi707k5s1rq4cij60d" MODIFIED="1545706367353" TEXT="・リテラシー教育"/></node><node CREATED="1545706367354" ID="73391o2kiatppgor8be23n8g16" MODIFIED="1545706367354" TEXT="ユーザ・顧客管理"><node CREATED="1545706367354" ID="792254gkvquhh23qvideahch5o" MODIFIED="1545706367354" TEXT="・ポリシー教育"/><node CREATED="1545706367354" ID="1hrl5407bmrgjm5utegip5fnkg" MODIFIED="1545706367354" TEXT="・情報取扱い規則"/></node><node CREATED="1545706367354" ID="7k7l0t4i7scvho27ckocmaocb9" MODIFIED="1545706367354" TEXT="コンテンツ管理"><node CREATED="1545706367354" ID="22m855tl55savlrfo9dsknrepo" MODIFIED="1545706367354" TEXT="・コンテンツ更新ルール"/></node></node></node></node><node CREATED="1545706367354" ID="7tu64g295j8tri78e3s48q1cet" MODIFIED="1545706367354" TEXT="【コラム】 「既知の脆弱性が存在するウェブサイトに関する届出」"/></node><node CREATED="1545706367354" ID="51c1pkk8upskj58sdnvvadl62u" MODIFIED="1545706367354" TEXT="3. セキュリティ対策要件および強化のポイント"><node CREATED="1545706367354" ID="1tgmtfcisdst2bd07m9pgdai24" MODIFIED="1545706367354" TEXT="各セキュリティ対策が、どのような要件を満たすべきか、どの程度の強度レベルを必要とするか、等については、構築するウェブサイトにおいて実現する機能やサービスや事業モデルによって、大きく異なってくる。"/><node CREATED="1545706367354" ID="6mpnl7tq43pbqklv43613pk22e" MODIFIED="1545706367354" TEXT="3.1. 実現する機能、サービスに対する考慮のポイント"><node CREATED="1545706367354" ID="4s0cjbnnh7tagrcnjq4h5pte6c" MODIFIED="1545706367354" TEXT="3.1.1 企業等の組織が公開するウェブサイトでのポイント"><node CREATED="1545706367354" ID="6dd0th27v0e66as5q4k8pau1v3" MODIFIED="1545706367354" TEXT="改ざんされることで、サイト利用者が不利益を被る場合が考えられる。また、偽の情報を掲載されてしまったことで、サイト運営者の信用失墜が発生する。"/><node CREATED="1545706367354" ID="4s2832u6gcr6rrcbo9mjqo9cuq" MODIFIED="1545706367354" TEXT="ウェブサイト改ざんの被害は、運営組織の大小や掲載されている情報の内容によらず、事業継続の大きな障害となりうる。このことから、「我社のウェブサイトには漏洩して問題がある情報はない」というケースにおいても、簡単に安心することはできない。"/><node CREATED="1545706367354" ID="0mh9vdtdtr5t1enl0db36a41gs" MODIFIED="1545706367354" TEXT="改ざん被害にあわないためには、ウェブサイトで使用しているソフトウェアを常に最新の状態に保つことが、第一の対策となる。"/><node CREATED="1545706367354" ID="5612f6nmqicpkq9ov6ua2vvugf" MODIFIED="1545706367354" TEXT="モールやASP の運営形態"><node CREATED="1545706367354" ID="788jmk5ckieo7n2q2igt0v2rgu" MODIFIED="1545706367354" TEXT="ソフトウェアのアップデートはサービス提供者が実施する対策"/><node CREATED="1545706367354" ID="0e4cdpl392mc5i8ts9l2kqj7qr" MODIFIED="1545706367354" TEXT="サービス提供者を選定する段階で、外部機関によるペネトレーションテストを定期的に実施していたり、ウェブサイト改ざん検知の機能を提供していたりするサービス提供者を選択することが重要"/><node CREATED="1545706367354" ID="22hfbet03tdsp7mr56osrvbfjo" MODIFIED="1545706367354" TEXT="例えば、「外部の●●社の脆弱性診断を毎年実施し、脆弱性が検出されないことを確認している」といった、明確な対策状況の回答が得られることが望ましい。"/></node></node><node CREATED="1545706367354" ID="6eqlsea7qp77soq6ircisijbk5" MODIFIED="1545706367354" TEXT="3.1.2 EC サイトでのポイント"><node CREATED="1545706367354" ID="6qd63eb2e0er0n02svc3riues2" MODIFIED="1545706367354" TEXT="EC サイト等では決済機能を提供するため、ウェブサイト上でクレジットカード情報や、口座情報等の取り扱いが必要である。万が一、EC サイトで不正アクセスや情報漏洩等が発生した場合、サイト利用者のみならず、関係している組織に対しても、重大な被害が発生する。"/><node CREATED="1545706367354" ID="6avq291ttmjracv6pirq8442r4" MODIFIED="1545706367354" TEXT="ASP やモール、SaaS 型クラウドサービスを利用して運営する場合"><node CREATED="1545706367354" ID="2q61uc22e9ah90hnk24u2umjhu" MODIFIED="1545706367354" TEXT="決済機能を含む様々なプログラムはサービス提供者によって実装されるものである。そのため、サイト運営者による攻撃を検知するためのプログラムの実装や、脆弱性の修正等の管理をすることができない"/><node CREATED="1545706367354" ID="1bed3hq9p2gm16hmi716lp35mq" MODIFIED="1545706367354" TEXT="サービス提供者を選択する時点で、外部からの攻撃を検知・遮断するための監視システムを導入しているか、業界団体や政府機関が定めるセキュリティ基準に準拠した対策を行っているかについて調査する必要がある。"/><node CREATED="1545706367354" ID="18arpgdvuvfe67vui15cd015bf" MODIFIED="1545706367354" TEXT="日本国内のサービス提供者であればPCI DSSへの準拠について確認が必須である38"><node CREATED="1545706367354" ID="0lk8e048tbr9354lb1othnls0v" MODIFIED="1545706367354" TEXT="PCI DSS は、国際クレジットカード国際カードブランド5 社が共同で設立したPCI SecurityStandards Council によって運用、管理されている基準であり、日本国内ではクレジットカード情報を自社内で取り扱う企業に対して、2018 年3 月までにPCI DSS へ準拠することが義務となっている。"/></node></node><node CREATED="1545706367354" ID="2gg3kcma4shoa3r5j8cij2p1gf" MODIFIED="1545706367354" TEXT="レンタルサーバやPaaS・IaaS 型のクラウドサービス、ハウジング、オンプレミスの運営形態の場合"><node CREATED="1545706367354" ID="680481edvm3iku07kgi8408rd9" MODIFIED="1545706367354" TEXT="決済機能はサイト運営者が何らかの形で導入する必要がある。たとえば、自組織で決済機能を開発する方法や、クレジットカード会社が提供するサービスを利用するといった方法が考えられる。"/><node CREATED="1545706367354" ID="6fg38vmh3krbt14snseareathc" MODIFIED="1545706367354" TEXT="ウェブサイトに独自に決済機能を開発する場合、自組織内で機微な情報を取り扱う必要が発生する。"/><node CREATED="1545706367354" ID="6tb46b32frs77k54cigbhfe96f" MODIFIED="1545706367354" TEXT="これによりPCI DSS 準拠へのセキュリティ対策・維持の負荷が増大するため、外部の決済代行会社に決済機能を委託することで、サイト運営者の負担を軽減することも一考である。"/><node CREATED="1545706367354" ID="4ueov4bifnp9gm602cpq20skj4" MODIFIED="1545706367354" TEXT="サイト運営者は悪意ある第三者がサイト利用者になりすまして商品の購入や、他者の決&#13;&#10;済情報を盗み取ることができないようにすることも必要である。"/><node CREATED="1545706367354" ID="33kaj1uco2mk8olu4a3uc9i7ds" MODIFIED="1545706367354" TEXT="正規の利用者になりすまして情報を盗み取る場合、正規のサイト利用者と攻撃者を識別することが困難である。"><node CREATED="1545706367354" ID="12qnm20bnkihui3k4cmucr6t0k" MODIFIED="1545706367354" TEXT="なりすましの被害を受ける原因"><node CREATED="1545706367354" ID="43kbnrpq4eb14k4aib4qem2g4j" MODIFIED="1545706367354" TEXT="1. 簡単で短いパスワードを設定していた"/><node CREATED="1545706367354" ID="3adohbig686vghire4pdn2c35u" MODIFIED="1545706367354" TEXT="2. 個人情報から類推しやすいパスワードを設定していた"/><node CREATED="1545706367354" ID="06n83a34njdnd9rlcdfei4id3q" MODIFIED="1545706367354" TEXT="3. 他のウェブサービスで同じパスワードを設定していた"/></node><node CREATED="1545706367354" ID="6vmt5tp5f3k5sb7rovphedbvd0" MODIFIED="1545706367354" TEXT="なりすましを防ぐためには、3.2 節で説明するサイト運営者側の対策だけでなく、サイト利用者に対して簡単なパスワードの禁止やパスワードの使いまわしをしないこと等の啓発が必要である。"/></node></node></node><node CREATED="1545706367354" ID="25p3uv25p0ibt8l0fjv499t2n6" MODIFIED="1545706367354" TEXT="3.1.3 SNS サイトや掲示板サイト等でのポイント"><node CREATED="1545706367354" ID="2c7rkgpfat4c8c93us70ud5j26" MODIFIED="1545706367354" TEXT="悪意ある書き込みや改ざんには十分留意する必要がある。"/><node CREATED="1545706367354" ID="18tn9edo6v06loikc29mprvcu2" MODIFIED="1545706367354" TEXT="例えば、クロスサイト・スクリプティングの脆弱性の場合"><node CREATED="1545706367354" ID="65fb5i874s2ujihc2onm2soitk" MODIFIED="1545706367354" TEXT="攻撃者がスクリプトを含む内容を投稿することで偽の情報を表示されてしまう。このような被害を防ぐために、投稿される内容にスクリプトが含まれていれば、ウェブサイト上にそのまま出力しないよう特定の記号はエンコードして表示する必要がある。"/></node><node CREATED="1545706367354" ID="0vbjltjtuedm7o82rtn6397edm" MODIFIED="1545706367354" TEXT="クロスサイト・リクエスト・フォージェリの脆弱性がある場合"><node CREATED="1545706367354" ID="2k7jmuhhuj1l9d0bja6uim1ekj" MODIFIED="1545706367354" TEXT="サイト利用者が意図しない操作を実行させられてしまう可能性が存在し、結果的に登録情報の書き換えや、意図しない内容を投稿させられてしまうといった被害につながる。"/><node CREATED="1545706367354" ID="2juu2s8ksrkhj6ab2pr0m45kic" MODIFIED="1545706367354" TEXT="これを防ぐために、登録情報の変更等の重要な処理の際にはパスワードの再入力を求めることや、外部のウェブサイトから転送された通信であるか調査する機能を実装する等の対策が必要である。"/></node><node CREATED="1545706367354" ID="6k1cqptej8iikdd8ljhh2mksr3" MODIFIED="1545706367354" TEXT="この他の攻撃"><node CREATED="1545706367354" ID="47n1att4vodbd41fts06vq8uqv" MODIFIED="1545706367354" TEXT="他者のセッションを乗っ取り、他人になりすまして記事を投稿する等がある。その対策としては、攻撃者によるセッションID の推測や、被害者に任意のセッションID を使用させない（セッションID の固定をさせない）ウェブアプリケーションの設計にすることが必要である。"/></node></node><node CREATED="1545706367354" ID="4qsnnmmjde0ivi8nk8pj0ggvps" MODIFIED="1545706367354" TEXT="3.1.4 画像投稿サイト等のファイルアップロードサイトでのポイント"><node CREATED="1545706367354" ID="4f704aglbme2nub40n2g9ebr55" MODIFIED="1545706367354" TEXT="違法なファイルをアップロードされる以外にも、実行形式のファイルをアップロードされてしまうことにより、アップロードサイトのサーバ上で不正なファイルを実行されてしまう可能性がある。"/><node CREATED="1545706367354" ID="1hqc6njuf3ckifp096dmg314js" MODIFIED="1545706367354" TEXT="このようなことが可能な場合、スクリプトを含むファイルをアップロードし、外部からそのファイルを参照することで、任意のスクリプトをサーバ上で実行されてしまう被害が考えられる。"/><node CREATED="1545706367354" ID="68m8kt1nenpscc42d9mgumj4iq" MODIFIED="1545706367354" TEXT="スクリプトを実行されることで、ウェブサイトの改ざんや、サーバの設定の改ざんにつながる可能性がある。"/><node CREATED="1545706367354" ID="7smp1ofqtkijcd9qtp93orm1n6" MODIFIED="1545706367354" TEXT="また、ウイルスを含むファイルをアップロードされた場合、サーバにウイルスが感染し、外部から不正な命令を受け付けるようにされてしまう可能性もある。"/><node CREATED="1545706367354" ID="1jmqkghum0gdcc1651nnubfgm3" MODIFIED="1545706367354" TEXT="このようなリスクを回避するために、アップロード可能なファイル形式を制限するだけでなく、アップロードされたサーバ上でファイルのウイルスチェックを行う等の対策を講じることが必要である。"/><node CREATED="1545706367354" ID="26saf3msttpioth0olq8cl5m3k" MODIFIED="1545706367354" TEXT="また、特定のユーザ以外にアクセスを許可しないファイルが、誰でもURL を直接指定することで閲覧できてしまうといった、アクセス制限不備の問題が考えられる。"/><node CREATED="1545706367354" ID="3o8go6mdv6okn4l0lmeg3lgghl" MODIFIED="1545706367354" TEXT="このような脆弱性がある場合、アップロードされたファイルが不特定多数のサイト利用者から参照可能になってしまう。"/><node CREATED="1545706367354" ID="53gh02tg3qr1k6r1pdl921hh1s" MODIFIED="1545706367354" TEXT="この場合、個人を特定できてしまう情報を含むファイルが、URL を直接指定するだけで参照できるといった被害が発生する。"/><node CREATED="1545706367354" ID="5jdsm9h1hl99d5qkj7t4dk5dfu" MODIFIED="1545706367354" TEXT="このような被害を防止するために、ファイルのアクセス制限を適切に設定することが求められる。"/></node></node><node CREATED="1545706367354" ID="49h0uaqufck1l2bjv8ffd0k240" MODIFIED="1545706367354" TEXT="3.2. セキュリティ強化のポイント"><node CREATED="1545706367354" ID="0q15el4bq0ql108igb355d0u12" MODIFIED="1545706367354" TEXT="技術的な対策の観点"><node CREATED="1545706367354" ID="71s70fk4vjs8nngrau1oh1avu1" MODIFIED="1545706367354" TEXT="(1) ネットワーク攻撃、不正アクセス対策"><node CREATED="1545706367354" ID="66ko94q2jc3otfjgi6m5koofcd" MODIFIED="1545706367354" TEXT="・外部からの攻撃が発生しているかの把握（攻撃の検知等）"><node CREATED="1545706367354" ID="2qeaneg989a55q4bj598prk957" MODIFIED="1545706367354" TEXT=""/></node><node CREATED="1545706367354" ID="3bh3u8ba8kme2tl520bdvvuihi" MODIFIED="1545706367354" TEXT="・外部からの攻撃からの防御（攻撃の遮断等）"><node CREATED="1545706367354" ID="30ejr6hlp2sn813eadk8gd1v56" MODIFIED="1545706367354" TEXT=""/></node></node><node CREATED="1545706367354" ID="4r4tpgb55au2qe2gslemoghimh" MODIFIED="1545706367354" TEXT="(2) DDoS攻撃対策"><node CREATED="1545706367354" ID="657a4ehptc9nr5hedfuav7snl4" MODIFIED="1545706367354" TEXT="・攻撃通信をネットワークに入れないためにどうするか"/></node><node CREATED="1545706367354" ID="7onos8qe3143uemn9fqc2m2mtj" MODIFIED="1545706367354" TEXT="(3) なりすまし対策"><node CREATED="1545706367354" ID="7l1hjsgtnq8hptho0335gkn1se" MODIFIED="1545706367354" TEXT="・パスワードの管理体制の強化"/><node CREATED="1545706367354" ID="5m30nfevieseb6771u36r0cafs" MODIFIED="1545706367354" TEXT="・使用する接続方式、認証方式の強化"/><node CREATED="1545706367354" ID="7aurchvv9a5qjd4l4momijf3cs" MODIFIED="1545706367354" TEXT="サイト利用者に普及・啓発すべき対策について以下で解説する。"><node CREATED="1545706367354" ID="74diln2lmoth589its886vc2dt" MODIFIED="1545706367354" TEXT="長く複雑なパスワードを設定するようにサイト利用者に要求すれば"><node CREATED="1545706367354" ID="6m1co7rib6bvlkhroh77adg98n" MODIFIED="1545706367354" TEXT="・パスワードを使いまわす可能性の増加"/><node CREATED="1545706367354" ID="1u9ci7rk99738umff917jnif91" MODIFIED="1545706367354" TEXT="・利便性の低下によるサイト利用者の減少"/></node><node CREATED="1545706367354" ID="4vaos8rj054s314aubl5vbcbj2" MODIFIED="1545706367354" TEXT="その一つの答えとして、次に解説する二要素認証や二段階認証が存在する。"/></node></node><node CREATED="1545706367354" ID="3kg9jlpobj34vkl835jii2ug9k" MODIFIED="1545706367354" TEXT="(4) 重要情報の保護対策"><node CREATED="1545706367354" ID="31jgevu7b03ji5rqjr7rrcttrg" MODIFIED="1545706367354" TEXT="・データベースをインターネット公開領域(いわゆるDMZ45)に配置しない"/><node CREATED="1545706367354" ID="5bh9ib3nq5vd8uf9qnhql57rs7" MODIFIED="1545706367354" TEXT="・重要情報の暗号化"/><node CREATED="1545706367354" ID="7ato3vsthehaf2h3a1679inali" MODIFIED="1545706367354" TEXT="・情報を変更、参照する際の再認証"/><node CREATED="1545706367354" ID="7tnt1j823m7obhk4d2qnh1qqnu" MODIFIED="1545706367354" TEXT="重要情報の暗号化について"/><node CREATED="1545706367354" ID="1t1tnebdbqigmsaluns9nocf1o" MODIFIED="1545706367354" TEXT="ウェブサイトに登録した情報を変更、参照する際の再認証の必要性について"/></node><node CREATED="1545706367354" ID="7kdhdktuspvqtt5reer8sispmu" MODIFIED="1545706367354" TEXT="(5) 事業継続対策"><node CREATED="1545706367354" ID="0gk10cgmp0vh95saa7tdlvp9uh" MODIFIED="1545706367354" TEXT="サーバ等の機材故障やインターネット上から攻撃を受けた場合にウェブサイトの停止期間を最小化するためには、事業継続計画（Business continuity planning）を検討する必要がある。"/><node CREATED="1545706367354" ID="4co3mtcc3eqsn8m82sivs02j9j" MODIFIED="1545706367354" TEXT="事業継続を目的とした対策には以下の２点等が存在"><node CREATED="1545706367354" ID="2pc86o6b1mffd786ph9f8muf2o" MODIFIED="1545706367354" TEXT="・予備システムを設置する"/><node CREATED="1545706367354" ID="25jbnj7u5k37stbosicq50g4i0" MODIFIED="1545706367354" TEXT="・バックアップを定期的に取得する"/></node><node CREATED="1545706367354" ID="5s2n8mvrao0d8ugb740l1hleub" MODIFIED="1545706367354" TEXT="・予備システムを設置する"><node CREATED="1545706367354" ID="2ghj403jt6dmdu2dhml2ap1rbk" MODIFIED="1545706367354" TEXT="インターネットからの攻撃が行われ、サーバが破壊されてしまった場合、データやサービスの復旧には長期間の作業が必要となる。このような場合に、破壊されたサーバと同一の構成の予備システムがあれば、ウェブサイト復旧までの時間を大幅に削減することが可能である。"/><node CREATED="1545706367354" ID="50mjnt9v9l4e6fmgaqap3n8ud6" MODIFIED="1545706367354" TEXT="注意すべき点として、予備システムを有効に活用するためには、主となるサーバと可能な限り同じデータを持ち、同一のソフトウェアバージョンに揃える必要がある。"/><node CREATED="1545706367354" ID="6g2ce40gff25lh4ge9t2gsr53p" MODIFIED="1545706367354" TEXT="また、予備システムが主となるサーバとは別の拠点に設置されていれば、大規模な地域災害時への対策にも繋がる。"/><node CREATED="1545706367354" ID="0us7dpqsk47gukvmjj2htnkun7" MODIFIED="1545706367354" TEXT="前述の予備システムを設置できない場合は、次に説明するバックアップの取得が重要になる。"/></node><node CREATED="1545706367354" ID="3jl6v32k8mmnnc2nche45e0et8" MODIFIED="1545706367354" TEXT="・バックアップを定期的に取得する"><node CREATED="1545706367354" ID="4jbf00vfvq3ep17bcea28ub1io" MODIFIED="1545706367354" TEXT="インターネット上から攻撃を受け、ウェブサーバが破壊された際に、ウェブサーバや重要情報のバックアップを取得していればバックアップデータを元にウェブサイトを速やかに復旧することができる。"/><node CREATED="1545706367354" ID="1f6toc7jv1gjf50975cp0gpopt" MODIFIED="1545706367354" TEXT="多くの場合、バックアップを取得するために専用のソフトウェアを購入する必要はなく、OS の機能としてバックアップを取得することができる。"/><node CREATED="1545706367354" ID="3655bl0aipgrlh96mmuu99pcum" MODIFIED="1545706367354" TEXT="しかし、バックアップデータを取得していても、ウェブサーバ上に保管していれば、ウェブサーバが攻撃を受けた際に同時にバックアップデータも破壊されてしまう可能性がある。"/><node CREATED="1545706367354" ID="6b91eibls7d7fmd8692ts6u3e9" MODIFIED="1545706367354" TEXT="また、バックアップデータを盗まれることで、攻撃者が偽サイトを作成することを可能にしてしまうことも考えられる。サーバ上に保管していない場合でも、ネットワークに接続された端末に保管されていれば、ウイルスがネットワークに侵入した際にバックアップデータが破壊・窃取を受ける可能性がある。"/><node CREATED="1545706367354" ID="0a6i4fqhba7q85b0qajhau6g53" MODIFIED="1545706367354" TEXT="以上の理由から、バックアップデータは外付けハードディスク等に保存し、ネットワークから切り離して保管しておくことが望ましい。"/></node></node></node><node CREATED="1545706367354" ID="5ch33oujdkrqi2575nehb23pd2" MODIFIED="1545706367354" TEXT="その他の対策の観点"><node CREATED="1545706367354" ID="0s6g9okrgqplf4qm676j1tk8aj" MODIFIED="1545706367354" TEXT="(1) ログの収集、分析"/><node CREATED="1545706367354" ID="5lpmlbuefmi16aobhdgu8662lf" MODIFIED="1545706367354" TEXT="(2) 各種基準への準拠"><node CREATED="1545706367354" ID="7vel5vs7q8qmsp8defi04klcgr" MODIFIED="1545706367354" TEXT="近年では、ウェブサイトへの攻撃による被害に対応した保険商品等が提供されている。しかし、このような保険に加入する場合は、保険企業やセキュリティ関連組織等が定める基準に準拠したウェブサイト運用が行われていることが条件となっている場合が多い。"/><node CREATED="1545706367354" ID="4dbo51e4amc7g05qppt5thpljq" MODIFIED="1545706367354" TEXT="また、ウェブサイトの性質やウェブサイト上で取り扱う情報によっては、所轄官庁が取り扱いの基準を定めている場合がある。"/><node CREATED="1545706367354" ID="1glt7qofpctmhdc7lts9a0vk7n" MODIFIED="1545706367354" TEXT="国内であれば、経済産業省やNISC46、海外であればNIST47等の公的機関がセキュリティ要件について様々なガイドラインや基準を定めている。"/><node CREATED="1545706367354" ID="40d51p4b8nkpfad76ho1smjcpo" MODIFIED="1545706367354" TEXT="サービス提供者を選択する際に注意すべき点として、以下の２点を挙げる。"><node CREATED="1545706367354" ID="1f0fcle5dd3osrg9m0svdvm7lm" MODIFIED="1545706367354" TEXT="・関係する団体が定める必須のセキュリティ基準を満たしているか"><node CREATED="1545706367354" ID="4e0c5i2jbnlslsv0r2i7b2p905" MODIFIED="1545706367354" TEXT="EC サイト等の決済機能を有するウェブサイトであれば、使用する決済機能を提供する企業や、業界団体が定めるセキュリティ基準基準を満たしているかについて確認が必要である。"><node CREATED="1545706367354" ID="688ubf44qd6vabi5md118eagpb" MODIFIED="1545706367354" TEXT="代表例として、PCI DSS がある。3.1.2 項でも解説した通り、国内でクレジットカードの決済機能を導入する場合は、PCI DSS に準拠することが求められる。"/></node><node CREATED="1545706367354" ID="600i16rmh762oje3gsn1bclpbk" MODIFIED="1545706367354" TEXT="この他に、サイト運営者が所属する業界や団体が定めるセキュリティ基準がないか確認し、その基準を満たしているサービス提供者を選択すべきである。"/></node><node CREATED="1545706367354" ID="6vmi3c2kmf849j7gpvjt249d7k" MODIFIED="1545706367354" TEXT="・必須ではないが、自発的にセキュリティ基準に則った対策を実施しているか"><node CREATED="1545706367354" ID="64qtf9ldbpmskhag6mr18njtuk" MODIFIED="1545706367354" TEXT="サービス提供者が自発的に何らかの基準に従ったセキュリティ対策を実施しているか、という観点で選択を行うべきである。"/><node CREATED="1545706367354" ID="555u6sqrsjsbv98gum3pbosilm" MODIFIED="1545706367354" TEXT="サービス提供者が自発的に何らかの基準に従ったセキュリティ対策を実施しているか"><node CREATED="1545706367354" ID="332vsb0qss9kdh6gb4rb2tmgmo" MODIFIED="1545706367354" TEXT="一例をあげると、モールやASP のサービス提供者を選択する場合であれば、総務省が定めた「ASP・SaaS における 情報セキュリティ対策ガイドライン」に準拠しているか、といった観点で確認することが考えられる。"/><node CREATED="1545706367354" ID="5f96nd9tka57l8tgbqvjdmilhu" MODIFIED="1545706367354" TEXT="これは、ASP やSaaS のサービス提供者に対し、実施すべきセキュリティ対策について解説した資料である。"/></node><node CREATED="1545706367354" ID="6106sm3r5hc3dl4d11da48169h" MODIFIED="1545706367354" TEXT="着目すべき基準については、ウェブサイトのサービス内容や運営形態によっても変わるため、サイト運営者自身で調査することが必要である。"/></node></node></node><node CREATED="1545706367354" ID="37ci2qi19fch0721uoc6um3u14" MODIFIED="1545706367354" TEXT="(3) セキュリティ専門事業者の活用"><node CREATED="1545706367354" ID="39ff5it1mao1qo2qa45e2ahptn" MODIFIED="1545706367354" TEXT="情報セキュリティを専門としない組織が独自に脆弱性診断や脆弱性対策を行うことは困難と考えられる。そのため、脆弱性の検査を行う情報セキュリティ専門の企業による診断を受けることが望ましい。"/><node CREATED="1545706367354" ID="48bkrq0uu49sucir02fh1gtknb" MODIFIED="1545706367354" TEXT="レンタルサーバ等では、サーバの構築をサイト運営者自身で行う必要がある。"/><node CREATED="1545706367354" ID="3rh1hgtn5vsm0asmq4rm7pj48r" MODIFIED="1545706367354" TEXT="また、サイト運営者によっては、使用するウェブアプリケーションを独自に開発する場合もあり、サーバや独自開発のウェブアプリケーションに脆弱性を作りこんでいないか、十分に検査する必要がある。"/><node CREATED="1545706367354" ID="0ot1nnjetsm9nk1ejrnt0oft4e" MODIFIED="1545706367354" TEXT="しかしながら、サイト運営者自身で脆弱性の検査を十分に行うことは困難であると考えられる。"/><node CREATED="1545706367354" ID="62qihq9ad7ot2t4apritsg57tn" MODIFIED="1545706367354" TEXT="特にハウジングやオンプレミスによる運営形態では、サーバだけでなくネットワーク機器等にも脆弱性がないか調査する必要がある。"/><node CREATED="1545706367354" ID="100m6uim7rbbmcp7eornjdg0bs" MODIFIED="1545706367354" TEXT="このような場合には、脆弱性の検査を専門としたセキュリティ企業に、サーバやウェブアプリケーションの脆弱性検査の委託を検討する必要がある。セキュリティ専門企業については、経済産業省が「情報セキュリティ監査企業台帳」を公開している。"/></node><node CREATED="1545706367354" ID="0fcunuv0aogf2bvis6692qukam" MODIFIED="1545706367354" TEXT="(4) サービス終了時の情報の破棄"><node CREATED="1545706367354" ID="6p2eitc1po562bis3q0gjnk7k2" MODIFIED="1545706367354" TEXT="サーバの機器故障やウェブアプリケーションの老朽化、事業方針の変更等により、ウェブサイトの運用の終了や、次期システムへ移行する時期が訪れる。"/><node CREATED="1545706367354" ID="56qf604n0rbvmtle906fr3toom" MODIFIED="1545706367354" TEXT="このような際に適切にデータの破棄が行われていない場合、ハードウェアを取得した第三者によって重要情報が盗み取られる被害が想定される。このような被害を防ぐため、重要情報や情報記録媒体の破棄について対応を検討しておく必要がある。"/><node CREATED="1545706367354" ID="1isicfm96vc612fjlhccsm3ohc" MODIFIED="1545706367354" TEXT="モールやASP、レンタルサーバ等ではウェブサイトのプログラムのみならず、サイト利用"/><node CREATED="1545706367354" ID="60upr3q5okf449qvu3ugb008vk" MODIFIED="1545706367354" TEXT="者の個人情報についても、サービス提供者の管理するサーバ上で管理される。そのため、ウェブサイトの運営を終了し、ASP 等の利用契約を解除する際、データの破棄・消去をサービス提供者がどのように取り扱いしているかについて、サービス提供者の選択時に確認する必要がある。"/><node CREATED="1545706367354" ID="2ej5il6as216uoiu9daoc2ha5f" MODIFIED="1545706367354" TEXT="例えば、使用していたサーバのハードディスクの破壊について証明書を発行する、米国国防総省が定めるDoD 5220.22-M に従ったデータの消去を保証しているといった観点がある。"/><node CREATED="1545706367354" ID="09fvd3ps9pl801d06hhoptqgk9" MODIFIED="1545706367354" TEXT="各組織がハードウェアを管理するオンプレミス等の運営形態の場合は、前述の規格に沿って組織内でデータの削除やハードディスクの破壊を実施、または専門業者に依頼しての実施を検討する必要がある。"/></node></node></node></node><node CREATED="1545706367354" ID="18knjgcc5g2qstpnnf6ccs01n6" MODIFIED="1545706367354" TEXT="おわりに"><node CREATED="1545706367354" ID="03bjg0cfmo5rv2f5ff4s243r18" MODIFIED="1545706367354" TEXT="ウェブサイトで提供したいサービスや運営形態の手軽さだけでなく、日々の運営でセキュリティを維持し続けることができるかといった点についても目を向けて頂き、安全なウェブサイトの運営が可能な運営形態を選定するようにしてほしい。"/></node><node CREATED="1545706367354" ID="2urbhtf68stvhfp41h1dt937h0" MODIFIED="1545706367354" TEXT="補足資料"><node CREATED="1545706367354" ID="10glq5lu6ddnrk8162uak9pfm8" MODIFIED="1545706367354" TEXT="【補足1】ウェブサイト構築・運営の委託について"><node CREATED="1545706367354" ID="0ml53lmih9te95ltrb1t81c1hp" MODIFIED="1545706367354" TEXT="中小企業をはじめとして、ウェブサイトの構築や運営を外部企業に委託する場合が数多く存在している。ASP やモールの運営形態が目的と合致せず、自組織内でウェブサイトを作成する技術がない場合は正しい選択である。"/><node CREATED="1545706367354" ID="0bndhe26783bq7gc18f6np0mnf" MODIFIED="1545706367354" TEXT="しかしながら、外部企業にウェブサイトの構築を委託した際、契約内容にウェブサイトの脆弱性検査や運用開始後に脆弱性が見つかった場合の対応が盛り込まれておらず、脆弱性を抱えたままコンテンツの更新だけが行われているウェブサイトが存在しており、IPA に対して届出が行われている。"/><node CREATED="1545706367354" ID="17920hme9jqa1i59oac81aint0" MODIFIED="1545706367354" TEXT="このようなウェブサイトを運営するサイト運営者に脆弱性の連絡を行った際、組織内で修正できないためウェブサイトを構築した企業に依頼する場合がある。"/><node CREATED="1545706367354" ID="1j6dvlc9vvh4goqdjfr5j163h9" MODIFIED="1545706367354" TEXT="しかし、修正のための追加費用が発生してしまうことが原因で対応が行われないという事態が発生している。"/><node CREATED="1545706367354" ID="4g9oaok9l6ccbmsfpaovk16e93" MODIFIED="1545706367354" TEXT="ウェブサイトに存在する脆弱性への対応は組織毎に判断すべき内容であるが、重大な問題を抱えたまま運営し、個人情報の流出等が発生する場合もあるため、可能な限り脆弱性は解消することが望ましい。"/><node CREATED="1545706367354" ID="0lin0srptne0n8bgqjju648aeo" MODIFIED="1545706367354" TEXT="前述したような事例となることを避けるために、ウェブサイト構築・運営を委託する際には、契約する際の項目に脆弱性の検査や脆弱性が見つかった場合の対応を明確に記載することが望ましいといえる。"/></node><node CREATED="1545706367354" ID="3vfpa83nrfba8afeqlndg1ts2g" MODIFIED="1545706367354" TEXT="【補足2】ログの取得について"><node CREATED="1545706367354" ID="2trli0htk02ticp8dpb378tpt1" MODIFIED="1545706367354" TEXT="ログイン履歴"/><node CREATED="1545706367354" ID="05u5s32qfif72ialhkpsgni0h4" MODIFIED="1545706367354" TEXT="アクセスログ"/><node CREATED="1545706367354" ID="6c49dcvs36of6n9gf27h34ppe1" MODIFIED="1545706367354" TEXT="通信ログ"/><node CREATED="1545706367354" ID="3gsb5am8hi2gh9fi7td3bb1iof" MODIFIED="1545706367354" TEXT="ログを取得する際は以下の点に注意する"><node CREATED="1545706367354" ID="6palcv4670tf568pd1899p6372" MODIFIED="1545706367354" TEXT="①意図しないログが残っていないか調査する"/><node CREATED="1545706367354" ID="01q205ck8j731bpu5bvqogsnlb" MODIFIED="1545706367354" TEXT="②取得場所以外の場所にログを保存する"/></node></node><node CREATED="1545706367354" ID="1f8fru9lesr02tkabob8cfm3ku" MODIFIED="1545706367354" TEXT="【補足3】バックアップの取得間隔について"><node CREATED="1545706367354" ID="3a48hn977i9v8uppk4ebk29172" MODIFIED="1545706367354" TEXT="実際にバックアップの取得期間をどのように設定するべきかについては、ウェブサイトの運用計画を定める段階で、ウェブサイトの目的や更新頻度等からどれぐらいの期間のデータの喪失を許容できるかといった観点で定めることが必要である。"/></node></node><node CREATED="1545706367354" ID="1qltgguvg8shcg0p06eu6o5qtm" MODIFIED="1545706367354" TEXT="【付録A】ウェブサイト構築・運営に関する参考資料"><node CREATED="1545706367354" ID="34hf0l4lmo2fjeb6jcegm1humj" MODIFIED="1545706367354" TEXT="「ウェブサイト構築のライフサイクル」の各フェーズで参考となる資料を一覧にまとめた表である。"/><node CREATED="1545706367354" ID="5ls0ct35d1kq45cbt6rctfogk4" MODIFIED="1545706367354" TEXT="1.企画"><node CREATED="1545706367354" ID="5l9g97j7ct9pdlpgss4mqmj58q" MODIFIED="1545706367354" TEXT="1 情報セキュリティ10 大脅威 2018"><node CREATED="1545706367354" ID="7ht8it3cg6ci22fuj1s40716i8" LINK="https://www.ipa.go.jp/security/vuln/10threats2017.html" MODIFIED="1545706367354" TEXT="https://www.ipa.go.jp/security/vuln/10threats2018.html"/><node CREATED="1545706367354" ID="4lf80qv5isgirhav4r263nmtls" MODIFIED="1545706367354" TEXT="IPA"/></node><node CREATED="1545706367354" ID="346hl3rrm7slggtebggqj3rn3l" MODIFIED="1545706367354" TEXT="2 情報セキュリティ白書 "><node CREATED="1545706367354" ID="7fgihler8qsastajoamtm274fh" LINK="https://www.ipa.go.jp/security/publications/hakusyo/2017.html" MODIFIED="1545706367354" TEXT="https://www.ipa.go.jp/security/publications/hakusyo/2017.html"/><node CREATED="1545706367354" ID="03mosogc9ddsfa25mg92gv9utq" MODIFIED="1545706367354" TEXT="IPA"/></node><node CREATED="1545706367354" ID="327d4jar21aj7en616ak5dujpk" MODIFIED="1545706367354" TEXT="3 中小企業の情報セキュリティ対策ガイドライン "><node CREATED="1545706367354" ID="5miqgu2hq7oasoi7h6h7l72hj6" LINK="https://www.ipa.go.jp/security/keihatsu/sme/guideline/" MODIFIED="1545706367354" TEXT="https://www.ipa.go.jp/security/keihatsu/sme/guideline/"/><node CREATED="1545706367354" ID="620qf0o0hftsdfp3uilv64qvr6" MODIFIED="1545706367354" TEXT="IPA"/></node><node CREATED="1545706367354" ID="6qelus2nt8mbkgbj7ssu08ph7n" MODIFIED="1545706367354" TEXT="4　中小企業のためのクラウドサービス安全利用の手引き"><node CREATED="1545706367354" ID="4lbejaag7k4ganqgjbb1h2bnqd" LINK="https://www.ipa.go.jp/security/cloud/tebiki_guide.html" MODIFIED="1545706367354" TEXT="https://www.ipa.go.jp/security/cloud/tebiki_guide.html"/><node CREATED="1545706367354" ID="60gnd953tc4q5mdq3gc3gt35ln" MODIFIED="1545706367354" TEXT="IPA"/></node><node CREATED="1545706367354" ID="4lvgkg4nd6o9p3pmfm2kvjcbc1" MODIFIED="1545706367354" TEXT="5 ウェブサイト構築事業者のための脆弱性対応ガイド "><node CREATED="1545706367354" ID="1ceqpqqjaip7sp2ebevmal4n6g" LINK="https://www.ipa.go.jp/security/fy20/reports/vuln_handling/index.html" MODIFIED="1545706367354" TEXT="https://www.ipa.go.jp/security/fy20/reports/vuln_handling/index.html"/><node CREATED="1545706367354" ID="1g1kq7qcpv2updgbj5o4d81n7k" MODIFIED="1545706367354" TEXT="IPA"/></node><node CREATED="1545706367354" ID="6m5p7q7tt2tqeo006jpuhnttkt" MODIFIED="1545706367354" TEXT="6 セキュリティ担当者のための脆弱性対応ガイド "><node CREATED="1545706367354" ID="0bui995fodsqv49dms0tmldqia" LINK="https://www.ipa.go.jp/security/fy22/reports/vuln_handling/index.html" MODIFIED="1545706367354" TEXT="https://www.ipa.go.jp/security/fy22/reports/vuln_handling/index.html"/><node CREATED="1545706367354" ID="7cb7660jdbdinc3hokfkd14f6p" MODIFIED="1545706367354" TEXT="IPA"/></node><node CREATED="1545706367354" ID="3r1m14195m0q64hcad1bsp90q5" MODIFIED="1545706367354" TEXT="7 ソフトウエア管理ガイドライン"><node CREATED="1545706367354" ID="4enqht5sg3dddfb0a0au512r89" LINK="http://www.meti.go.jp/policy/netsecurity/do